mapper.select:
SELECT * FROM carousel_info

    
        AND status = #{status}
    

ORDER BY sort_order,create_time DESC

mapper.insert:
INSERT INTO carousel_info (
    id, img_path, title, link, sort_order, create_time, create_username, update_time, update_username, status
)
