mapper.select:
SELECT t.*,u.real_name, u.id_card, u.phone, u.work_unit,ei.people_type,ei.annex_url, u.create_time AS user_create_time,ep.start_time,ep.end_time FROM exam_apply_info t LEFT JOIN exam_info e ON e.id = t.exam_id LEFT JOIN user_base_info u ON u.id = t.user_id LEFT JOIN exam_period_info ep ON ep.id = e.exam_period_id LEFT JOIN user_login_info ei ON ei.user_id = t.user_id
AND ei.people_type = #{peopleType}
AND u.real_name = #{realName}
AND u.id_card = #{idCard}
AND t.user_id = #{userId}
AND t.status = #{status}
AND (t.exam_id IS NULL OR t.exam_id = '' OR ep.start_time > #{createTime})
AND (t.exam_id IS NULL OR t.exam_id = '')
AND (ep.start_time > #{createTime})
AND (u.real_name LIKE CONCAT('%',#{search},'%') OR u.id_card LIKE CONCAT('%',#{search},'%') OR u.phone LIKE CONCAT('%',#{search},'%') OR u.work_unit LIKE CONCAT('%',#{search},'%'))
ORDER BY t.create_time DESC

mapper.insert:
INSERT INTO exam_apply_info (
    id, user_id, apply_time, exam_id, create_time, create_username, update_time, update_username, status
)
VALUES(
    #{id}, #{userId}, #{applyTime}, #{examId}, #{createTime}, #{createUsername}, #{updateTime}, #{updateUsername}, #{status}
)

mapper.update:
UPDATE exam_apply_info SET
    exam_id = #{examId},
    update_time = #{updateTime},
    update_username = #{updateUsername}
WHERE id = #{id}
