mapper.select:
SELECT t.*,ta1.AREA_NAME AS province_name, ta2.AREA_NAME AS city_name, ta3.AREA_NAME AS area_name, (SELECT COUNT(1) FROM exam_info e1 WHERE e1.exam_period_id = t.id) AS total_num, (SELECT COUNT(1) FROM exam_info e2 WHERE e2.exam_period_id = t.id AND e2.result = 1) AS pass_num, (SELECT COUNT(1) FROM exam_info e3 WHERE e3.exam_period_id = t.id AND e3.result IS NULL) AS null_num FROM exam_period_info t LEFT JOIN traffic_area ta1 ON ta1.area_no = t.province LEFT JOIN traffic_area ta2 ON ta2.area_no = t.city LEFT JOIN traffic_area ta3 ON ta3.area_no = t.area
AND start_time > #{startTime}
AND end_time < #{endTime}
AND (t.period LIKE CONCAT('%',#{search},'%') OR t.title LIKE CONCAT('%',#{search},'%') OR t.address LIKE CONCAT('%',#{search},'%'))
AND start_time > #{createTime}
AND start_time < #{createTime} AND end_time > #{createTime}
AND end_time < #{createTime}
ORDER BY create_time DESC

mapper.insert:
INSERT INTO exam_period_info (
    id, period, title, start_time, end_time, province, city, area, address, create_time, create_username, update_time, update_username, status
)
VALUES(
    #{id}, #{period}, #{title}, #{startTime}, #{endTime}, #{province}, #{city}, #{area}, #{address}, #{createTime}, #{createUsername}, #{updateTime}, #{updateUsername}, #{status}
)
