mapper.select:
SELECT info.*,ub.real_name realName,ub.id_card idCard,ub.phone phone,ub.people_type1,ub.people_type2 FROM income_info info LEFT JOIN user_base_info ub ON info.user_base_id = ub.id
AND ( ub.real_name like CONCAT('%',#{search},'%') OR ub.id_card like CONCAT('%',#{search},'%') OR ub.phone like CONCAT('%',#{search},'%') OR ub.people_type1 like CONCAT('%',#{search},'%') OR ub.people_type2 like CONCAT('%',#{search},'%'))
AND info.user_base_id = #{userBaseId}
AND info.status = #{status}
ORDER BY info.create_time DESC

mapper.insert:
INSERT INTO income_info (
id, title, user_base_id, out_trade_no,trade_no, money_amount, pay_time, status, create_time, update_time, create_username,update_username,remark
)
VALUES(
#{id}, #{title}, #{userBaseId}, #{outTradeNo},#{tradeNo}, #{moneyAmount}, #{payTime}, #{status}, #{createTime}, #{updateTime}, #{createUsername}, #{updateUsername},#{remark}
)

mapper.update:
UPDATE income_info SET
update_time = #{updateTime}

	,title = #{title}


	,user_base_id = #{userBaseId}


	,out_trade_no = #{outTradeNo}


	,money_amount = #{moneyAmount}


	,pay_time = #{payTime}


	,status = #{status}


	,create_username = #{createUsername}


	,update_username = #{updateUsername}


	,trade_no = #{tradeNo}


	,remark = #{remark}

WHERE id = #{id}
